INSERT INTO `drug_label` (`id`,`section_header`,`section_number`,`section_detail`,`drugs_id`) VALUES (1,'INDICATIONS & USAGE SECTION',1,'<!DOCTYPE html PUBLIC \"-\">\n<html>\n<head>\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\">\n<meta name=\"documentId\" content=\"\">\n<meta name=\"documentSetId\" content=\"\">\n<meta name=\"documentVersionNumber\" content=\"\">\n<meta name=\"documentEffectiveTime\" content=\"\">\n<title></title>\n<link rel=\"stylesheet\" type=\"text/css\" href=\"http://www.accessdata.fda.gov/spl/stylesheet/spl.css\" charset=\"UTF-8\">\n<script src=\"http://www.accessdata.fda.gov/spl/stylesheet/spl.js\" type=\"text/javascript\" charset=\"utf-8\">/* */</script>\n</head>\n<body class=\"spl\" id=\"spl\" onload=\'if(typeof convertToTwoColumns == \"function\")convertToTwoColumns();\'>\n<div class=\"DocumentTitle\"><p class=\"DocumentTitle\"><br></p></div>\n<h1></h1>\n<div class=\"Contents\">\n<p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">MEKINIST® is indicated, as a single agent or in combination with dabrafenib, for the treatment of patients with unresectable or metastatic melanoma with BRAF V600E or V600K mutations, as detected by an FDA-approved test <span class=\"Italics\">[see Clinical Studies (14.1)]</span>.</span></p>\n<p style=\"border-left:1px solid;\"><span class=\"XmChange\">Limitation of use: MEKINIST is not indicated for treatment of patients who have received prior BRAF-inhibitor therapy <span class=\"Italics\">[see Clinical Studies (14.2)]</span>.</span></p>\n</div>\n<div class=\"DataElementsTables\"></div>\n<p><div class=\"EffectiveDate\"></div> <div class=\"DistributorName\"></div></p>\n</body>\n</html>\n<!DOCTYPE html PUBLIC \"-\">\n<html>\n<head>\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\">\n<meta name=\"documentId\" content=\"\">\n<meta name=\"documentSetId\" content=\"\">\n<meta name=\"documentVersionNumber\" content=\"\">\n<meta name=\"documentEffectiveTime\" content=\"\">\n<title></title>\n<link rel=\"stylesheet\" type=\"text/css\" href=\"http://www.accessdata.fda.gov/spl/stylesheet/spl.css\" charset=\"UTF-8\">\n<script src=\"http://www.accessdata.fda.gov/spl/stylesheet/spl.js\" type=\"text/javascript\" charset=\"utf-8\">/* */</script>\n</head>\n<body class=\"spl\" id=\"spl\" onload=\'if(typeof convertToTwoColumns == \"function\")convertToTwoColumns();\'>\n<div class=\"DocumentTitle\"><p class=\"DocumentTitle\"><br></p></div>\n<h1></h1>\n<div class=\"Contents\">\n<p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">MEKINIST® is indicated, as a single agent or in combination with dabrafenib, for the treatment of patients with unresectable or metastatic melanoma with BRAF V600E or V600K mutations, as detected by an FDA-approved test <span class=\"Italics\">[see Clinical Studies (14.1)]</span>.</span></p>\n<p style=\"border-left:1px solid;\"><span class=\"XmChange\">Limitation of use: MEKINIST is not indicated for treatment of patients who have received prior BRAF-inhibitor therapy <span class=\"Italics\">[see Clinical Studies (14.2)]</span>.</span></p>\n</div>\n<div class=\"DataElementsTables\"></div>\n<p><div class=\"EffectiveDate\"></div> <div class=\"DistributorName\"></div></p>\n</body>\n</html>\n',1),(2,'DOSAGE & ADMINISTRATION SECTION',2,'<!DOCTYPE html PUBLIC \"-\">\n<html>\n<head>\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\">\n<meta name=\"documentId\" content=\"\">\n<meta name=\"documentSetId\" content=\"\">\n<meta name=\"documentVersionNumber\" content=\"\">\n<meta name=\"documentEffectiveTime\" content=\"\">\n<title></title>\n<link rel=\"stylesheet\" type=\"text/css\" href=\"http://www.accessdata.fda.gov/spl/stylesheet/spl.css\" charset=\"UTF-8\">\n<script src=\"http://www.accessdata.fda.gov/spl/stylesheet/spl.js\" type=\"text/javascript\" charset=\"utf-8\">/* */</script>\n</head>\n<body class=\"spl\" id=\"spl\" onload=\'if(typeof convertToTwoColumns == \"function\")convertToTwoColumns();\'>\n<div class=\"DocumentTitle\"><p class=\"DocumentTitle\"><br></p></div>\n<h1></h1>\n<div class=\"Contents\">\n<p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">Select patients for treatment of unresectable or metastatic melanoma with MEKINIST based on the presence of BRAF V600E or V600K mutation in tumor specimens <span class=\"Italics\">[see Clinical Studies (14.1)]</span>. Information on FDA-approved tests for the detection of BRAF V600 mutations in melanoma is available at: </span><a href=\"http://www.fda.gov/CompanionDiagnostics\">http://www.fda.gov/CompanionDiagnostics</a>.</p>\n<p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">The recommended dosage regimen is MEKINIST 2 mg orally taken once daily at the same time each day as a single agent or with dabrafenib. Continue treatment until disease progression or unacceptable toxicity occurs. </span></p>\n<p style=\"border-left:1px solid;\"><span class=\"XmChange\">Take MEKINIST at least 1 hour before or 2 hours after a meal <span class=\"Italics\">[see Clinical Pharmacology (12.3)]</span>. Do not take a missed dose of MEKINIST within 12 hours of the next dose of MEKINIST.</span></p>\n<p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">Review the Full Prescribing Information for dabrafenib for recommended dose modifications. Dose modifications are not recommended for MEKINIST when administered with dabrafenib for the following adverse reactions of dabrafenib: non-cutaneous malignancies and uveitis.</span></p>\n<p style=\"border-left:1px solid;\"><span class=\"XmChange\"><span class=\"Underline\">For New Primary Cutaneous Malignancies</span></span></p>\n<p style=\"border-left:1px solid;\"><span class=\"XmChange\">No dose modifications are required.</span></p>\n<table width=\"600pt\">\n<caption><span>Table 1. Recommended Dose Reductions</span></caption>\n<col width=\"40%\">\n<col width=\"60%\">\n<tbody class=\"Headless\">\n<tr class=\"First\"><td class=\"Botrule Lrule Rrule Toprule\" colspan=\"2\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\"><span class=\"Bold\">Dose Reductions for MEKINIST </span></span></p></td></tr>\n<tr>\n<td class=\"Botrule Lrule Rrule\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">First Dose Reduction</span></p></td>\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">1.5 mg orally once daily</span></p></td>\n</tr>\n<tr>\n<td class=\"Botrule Lrule Rrule\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">Second Dose Reduction</span></p></td>\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">1 mg orally once daily</span></p></td>\n</tr>\n<tr class=\"Last\">\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">Subsequent Modification</span></p></td>\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">Permanently discontinue if unable to tolerate MEKINIST 1 mg orally once daily</span></p></td>\n</tr>\n</tbody>\n</table>\n<table width=\"800pt\">\n<caption><span>Table 2. Recommended Dose Modifications for MEKINIST</span></caption>\n<col width=\"50%\">\n<col width=\"50%\">\n<tfoot><tr class=\"First Last\"><td colspan=\"2\">\n<p class=\"First\"><span class=\"Sup\">a</span>National Cancer Institute Common Terminology Criteria for Adverse Events (NCI CTCAE) version 4.0.\n											</p>\n<p><span class=\"Sup\">b</span>See Table 1 for recommended dose reductions of MEKINIST.</p>\n</td></tr></tfoot>\n<tbody class=\"Headless\">\n<tr class=\"First\">\n<td class=\"Botrule Lrule Rrule Toprule\" align=\"center\" valign=\"bottom\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"Bold\"><span class=\"XmChange\">Severity of Adverse Reaction<span class=\"Sup\">a</span></span></span></p></td>\n<td class=\"Botrule Lrule Rrule Toprule\" align=\"center\" valign=\"bottom\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"Bold\"><span class=\"XmChange\">MEKINIST<span class=\"Sup\">b</span></span></span></p></td>\n</tr>\n<tr><td class=\"Botrule Lrule Rrule\" colspan=\"2\" valign=\"middle\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"Italics\"><span class=\"XmChange\">Febrile Drug Reaction</span></span></p></td></tr>\n<tr>\n<td class=\"Botrule Lrule Rrule\" valign=\"top\"><dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Fever higher than 104°F</span></dd>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Fever complicated by rigors, hypotension, dehydration, or renal failure</span></dd>\n</dl></td>\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">Withhold MEKINIST until fever resolves. Then resume MEKINIST at same or lower dose level.</span></p></td>\n</tr>\n<tr><td class=\"Botrule Lrule Rrule\" colspan=\"2\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"Italics\"><span class=\"XmChange\">Cutaneous</span></span></p></td></tr>\n<tr>\n<td class=\"Botrule Lrule Rrule\" valign=\"top\"><dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Intolerable Grade 2 skin toxicity </span></dd>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Grade 3 or 4 skin toxicity</span></dd>\n</dl></td>\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\">\n<p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">Withhold MEKINIST for up to 3 weeks.</span></p>\n<dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">If improved, resume at a lower dose level. </span></dd>\n<dt>•</dt>\n<dd><span class=\"XmChange\">If not improved, permanently discontinue.</span></dd>\n</dl>\n</td>\n</tr>\n<tr><td class=\"Botrule Lrule Rrule\" colspan=\"2\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"Italics\"><span class=\"XmChange\">Cardiac</span></span></p></td></tr>\n<tr>\n<td class=\"Botrule Lrule Rrule\" valign=\"top\"><dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Asymptomatic, absolute decrease in left ventricular ejection fraction (LVEF) of 10% or greater from baseline and is below institutional lower limits of normal (LLN) from pretreatment value</span></dd>\n</dl></td>\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\"><dl>\n<dt> </dt>\n<dd><span class=\"XmChange\">Withhold MEKINIST for up to 4 weeks.</span></dd>\n<dt>•</dt>\n<dd><span class=\"XmChange\">If improved to normal LVEF value, resume at a lower dose level. </span></dd>\n<dt>•</dt>\n<dd><span class=\"XmChange\">If not improved to normal LVEF value, permanently discontinue.</span></dd>\n</dl></td>\n</tr>\n<tr>\n<td class=\"Botrule Lrule Rrule\" valign=\"top\"><dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Symptomatic congestive heart failure</span></dd>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Absolute decrease in LVEF of greater than 20% from baseline that is below LLN</span></dd>\n</dl></td>\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">Permanently discontinue MEKINIST.</span></p></td>\n</tr>\n<tr><td class=\"Botrule Lrule Rrule\" colspan=\"2\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"Italics\"><span class=\"XmChange\">Venous Thromboembolism</span></span></p></td></tr>\n<tr>\n<td class=\"Botrule Lrule Rrule\" valign=\"top\"><dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Uncomplicated DVT or PE</span></dd>\n</dl></td>\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\">\n<p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">Withhold MEKINIST for up to 3 weeks.</span></p>\n<dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">If improved to Grade 0-1, resume at a lower dose level. </span></dd>\n<dt>•</dt>\n<dd><span class=\"XmChange\">If not improved, permanently discontinue.</span></dd>\n</dl>\n</td>\n</tr>\n<tr>\n<td class=\"Botrule Lrule Rrule\" valign=\"top\"><dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Life threatening PE</span></dd>\n</dl></td>\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">Permanently discontinue MEKINIST.</span></p></td>\n</tr>\n<tr><td class=\"Botrule Lrule Rrule\" colspan=\"2\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"Italics\"><span class=\"XmChange\">Ocular Toxicities</span></span></p></td></tr>\n<tr>\n<td class=\"Botrule Lrule Rrule\" valign=\"top\"><dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Retinal pigment epithelial detachments (RPED)</span></dd>\n</dl></td>\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\">\n<p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">Withhold MEKINIST for up to 3 weeks.</span></p>\n<dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">If improved, resume MEKINIST at same or lower dose level. </span></dd>\n<dt>•</dt>\n<dd><span class=\"XmChange\">If not improved, discontinue or resume at a lower dose.</span></dd>\n</dl>\n</td>\n</tr>\n<tr>\n<td class=\"Botrule Lrule Rrule\" valign=\"top\"><dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Retinal vein occlusion</span></dd>\n</dl></td>\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">Permanently discontinue MEKINIST.</span></p></td>\n</tr>\n<tr><td class=\"Botrule Lrule Rrule\" colspan=\"2\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"Italics\"><span class=\"XmChange\">Pulmonary</span></span></p></td></tr>\n<tr>\n<td class=\"Botrule Lrule Rrule\" valign=\"top\"><dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Interstitial lung disease/pneumonitis</span></dd>\n</dl></td>\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">Permanently discontinue MEKINIST.</span></p></td>\n</tr>\n<tr><td class=\"Botrule Lrule Rrule\" colspan=\"2\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"Italics\"><span class=\"XmChange\">Other</span></span></p></td></tr>\n<tr>\n<td class=\"Botrule Lrule Rrule\" valign=\"top\"><dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Intolerable Grade 2 adverse reactions</span></dd>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Any Grade 3 adverse reactions</span></dd>\n</dl></td>\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\">\n<p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">Withhold MEKINIST </span></p>\n<dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">If improved to Grade 0-1, resume at a lower dose level. </span></dd>\n<dt>•</dt>\n<dd><span class=\"XmChange\">If not improved, permanently discontinue.</span></dd>\n</dl>\n</td>\n</tr>\n<tr>\n<td class=\"Botrule Lrule Rrule\" valign=\"top\"><dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">First occurrence of any Grade 4 adverse reaction</span></dd>\n</dl></td>\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\">\n<dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Withhold MEKINIST until adverse reaction improves to Grade 0-1. Then resume at a lower dose level.</span></dd>\n</dl>\n<p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">Or</span></p>\n<dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Permanently discontinue. </span></dd>\n</dl>\n</td>\n</tr>\n<tr class=\"Last\">\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\"><dl>\n<dt>•</dt>\n<dd><span class=\"XmChange\">Recurrent Grade 4 adverse reaction</span></dd>\n</dl></td>\n<td class=\"Botrule Lrule Rrule Toprule\" valign=\"top\"><p class=\"First\" style=\"border-left:1px solid;\"><span class=\"XmChange\">Permanently discontinue MEKINIST.</span></p></td>\n</tr>\n</tbody>\n</table>\n</div>\n<div class=\"DataElementsTables\"></div>\n<p><div class=\"EffectiveDate\"></div> <div class=\"DistributorName\"></div></p>\n</body>\n</html>\n',1),(3,'CONTRAINDICATIONS SECTION',4,'<!DOCTYPE html PUBLIC \"-\">\n<html>\n<head>\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\">\n<meta name=\"documentId\" content=\"\">\n<meta name=\"documentSetId\" content=\"\">\n<meta name=\"documentVersionNumber\" content=\"\">\n<meta name=\"documentEffectiveTime\" content=\"\">\n<title></title>\n<link rel=\"stylesheet\" type=\"text/css\" href=\"http://www.accessdata.fda.gov/spl/stylesheet/spl.css\" charset=\"UTF-8\">\n<script src=\"http://www.accessdata.fda.gov/spl/stylesheet/spl.js\" type=\"text/javascript\" charset=\"utf-8\">/* */</script>\n</head>\n<body class=\"spl\" id=\"spl\" onload=\'if(typeof convertToTwoColumns == \"function\")convertToTwoColumns();\'>\n<div class=\"DocumentTitle\"><p class=\"DocumentTitle\"><br></p></div>\n<h1></h1>\n<div class=\"Contents\"><p class=\"First\">None.</p></div>\n<div class=\"DataElementsTables\"></div>\n<p><div class=\"EffectiveDate\"></div> <div class=\"DistributorName\"></div></p>\n</body>\n</html>\n',1),(4,'DRUG INTERACTIONS SECTION',7,'<!DOCTYPE html PUBLIC \"-\">\n<html>\n<head>\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\">\n<meta name=\"documentId\" content=\"\">\n<meta name=\"documentSetId\" content=\"\">\n<meta name=\"documentVersionNumber\" content=\"\">\n<meta name=\"documentEffectiveTime\" content=\"\">\n<title></title>\n<link rel=\"stylesheet\" type=\"text/css\" href=\"http://www.accessdata.fda.gov/spl/stylesheet/spl.css\" charset=\"UTF-8\">\n<script src=\"http://www.accessdata.fda.gov/spl/stylesheet/spl.js\" type=\"text/javascript\" charset=\"utf-8\">/* */</script>\n</head>\n<body class=\"spl\" id=\"spl\" onload=\'if(typeof convertToTwoColumns == \"function\")convertToTwoColumns();\'>\n<div class=\"DocumentTitle\"><p class=\"DocumentTitle\"><br></p></div>\n<h1></h1>\n<div class=\"Contents\"><p class=\"First\">No formal clinical trials have been conducted to evaluate human cytochrome P450 (CYP) enzyme-mediated drug interactions with trametinib <span class=\"Italics\">[see Clinical Pharmacology (12.3)]</span>.</p></div>\n<div class=\"DataElementsTables\"></div>\n<p><div class=\"EffectiveDate\"></div> <div class=\"DistributorName\"></div></p>\n</body>\n</html>\n',1),(5,'INFORMATION FOR PATIENTS SECTION',16,'<!DOCTYPE html PUBLIC \"-\">\n<html>\n<head>\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\">\n<meta name=\"documentId\" content=\"\">\n<meta name=\"documentSetId\" content=\"\">\n<meta name=\"documentVersionNumber\" content=\"\">\n<meta name=\"documentEffectiveTime\" content=\"\">\n<title></title>\n<link rel=\"stylesheet\" type=\"text/css\" href=\"http://www.accessdata.fda.gov/spl/stylesheet/spl.css\" charset=\"UTF-8\">\n<script src=\"http://www.accessdata.fda.gov/spl/stylesheet/spl.js\" type=\"text/javascript\" charset=\"utf-8\">/* */</script>\n</head>\n<body class=\"spl\" id=\"spl\" onload=\'if(typeof convertToTwoColumns == \"function\")convertToTwoColumns();\'>\n<div class=\"DocumentTitle\"><p class=\"DocumentTitle\"><br></p></div>\n<h1></h1>\n<div class=\"Contents\">\n<p class=\"First\">Advise the patient to read the FDA-approved patient labeling (Patient Information).</p>\n<p>Inform patients of the following:</p>\n<p><span class=\"Underline\">Confirmation of BRAF V600E or V600K mutation</span></p>\n<p>Evidence of BRAF V600E or V600K mutation within the tumor specimen is necessary to identify patients for whom treatment with MEKINIST is indicated <span class=\"Italics\">[see Dosage and Administration (2.1)]</span>.</p>\n<p><span class=\"Underline\">New cutaneous and non-cutaneous malignancies</span></p>\n<p>MEKINIST administered with dabrafenib can result in the development of new primary cutaneous and non-cutaneous malignancies. Advise patients to contact their doctor immediately for any new lesions, changes to existing lesions on their skin, or other signs and symptoms of malignancies <span class=\"Italics\">[see Warnings and Precautions (5.1)]</span>.</p>\n<p><span class=\"Underline\">Hemorrhage</span></p>\n<p>MEKINIST administered with dabrafenib increases the risk of intracranial and gastrointestinal hemorrhage. Advise patients to contact their healthcare provider to seek immediate medical attention for signs or symptoms of unusual bleeding or hemorrhage <span class=\"Italics\">[see Warnings and Precautions (5.2)]</span>.</p>\n<p><span class=\"Underline\">Venous thrombosis</span></p>\n<p>MEKINIST administered with dabrafenib increases the risks of pulmonary embolism and deep venous thrombosis. Advise patients to seek immediate medical attention for sudden onset of difficulty breathing, leg pain, or swelling <span class=\"Italics\">[see Warnings and Precautions (5.3)]</span>.</p>\n<p><span class=\"Underline\">Cardiomyopathy</span></p>\n<p>MEKINIST can cause cardiomyopathy. Advise patients to immediately report any signs or symptoms of heart failure to their healthcare provider <span class=\"Italics\">[see Warnings and Precautions (5.4)]</span>.</p>\n<p><span class=\"Underline\">Retinal Pigment Epithelial Detachment</span></p>\n<p>MEKINIST can cause severe visual disturbances that can lead to blindness. Advise patients to contact their healthcare provider if they experience any changes in their vision <span class=\"Italics\">[see Warnings and Precautions (5.5)]</span>.</p>\n<p><span class=\"Underline\">Interstitial lung disease</span></p>\n<p>MEKINIST can cause interstitial lung disease (or pneumonitis). Advise patients to contact their healthcare provider as soon as possible if they experience signs such as cough or dyspnea <span class=\"Italics\">[see Warnings and Precautions (5.6)]</span>.</p>\n<p><span class=\"Underline\">Serious febrile reactions</span></p>\n<p>MEKINIST administered with dabrafenib can cause serious febrile reactions. Instruct patients to contact their healthcare provider if they develop fever while taking MEKINIST with dabrafenib <span class=\"Italics\">[see Warnings and Precautions (5.7)].</span></p>\n<p><span class=\"Underline\">Serious skin toxicities</span></p>\n<p>MEKINIST can cause serious skin toxicities which may require hospitalization. Advise patients to contact their healthcare provider for progressive or intolerable rash <span class=\"Italics\">[see Warnings and Precautions (5.8)]</span>.</p>\n<p><span class=\"Underline\">Hypertension</span></p>\n<p>MEKINIST can cause hypertension. Advise patients that they need to undergo blood pressure monitoring and to contact their healthcare provider if they develop symptoms of hypertension such as severe headache, blurry vision, or dizziness. </p>\n<p><span class=\"Underline\">Diarrhea</span></p>\n<p>MEKINIST often causes diarrhea which may be severe in some cases. Inform patients of the need to contact their healthcare provider if severe diarrhea occurs during treatment.</p>\n<p><span class=\"Underline\">Embryo-fetal Toxicity</span></p>\n<p>MEKINIST can cause fetal harm if taken during pregnancy. Advise a pregnant woman of the potential risk to a fetus <span class=\"Italics\">[see</span><span class=\"Italics\"> Warnings and Precautions (5.10), Use in Specific Populations (8.1, 8.3)]</span>.</p>\n<p><span class=\"Underline\">Females and males of reproductive potential</span></p>\n<p>Instruct females of reproductive potential to use highly effective contraception during treatment with MEKINIST and for 4 months after the last dose. Advise patients to contact their healthcare provider if they become pregnant, or if pregnancy is suspected, while taking MEKINIST <span class=\"Italics\">[see Warnings and Precautions (5.10), Use in Specific Populations (8.1, 8.3)]</span>.</p>\n<p><span class=\"Underline\">Lactation</span></p>\n<p>Advise women not to breastfeed during treatment with MEKINIST and for 4 months after the last dose <span class=\"Italics\">[see Use in Specific Populations (8.2)]</span>.</p>\n<p><span class=\"Underline\">Infertility</span></p>\n<p>Advise males and females of reproductive potential of the potential risk for impaired fertility <span class=\"Italics\">[see Use in Specific Populations (8.3)]</span>.</p>\n<p><span class=\"Underline\">Instructions for taking MEKINIST</span></p>\n<p>MEKINIST should be taken at least 1 hour before or at least 2 hours after a meal <span class=\"Italics\">[see Dosage and Administration (2.2)]</span>.</p>\n</div>\n<div class=\"DataElementsTables\"></div>\n<p><div class=\"EffectiveDate\"></div> <div class=\"DistributorName\"></div></p>\n</body>\n</html>\n',1),(6,'ADVERSE REACTIONS SECTION',6,'<!DOCTYPE html PUBLIC \"-\">\n<html>\n<head>\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\">\n<meta name=\"documentId\" content=\"\">\n<meta name=\"documentSetId\" content=\"\">\n<meta name=\"documentVersionNumber\" content=\"\">\n<meta name=\"documentEffectiveTime\" content=\"\">\n<title></title>\n<link rel=\"stylesheet\" type=\"text/css\" href=\"http://www.accessdata.fda.gov/spl/stylesheet/spl.css\" charset=\"UTF-8\">\n<script src=\"http://www.accessdata.fda.gov/spl/stylesheet/spl.js\" type=\"text/javascript\" charset=\"utf-8\">/* */</script>\n</head>\n<body class=\"spl\" id=\"spl\" onload=\'if(typeof convertToTwoColumns == \"function\")convertToTwoColumns();\'>\n<div class=\"DocumentTitle\"><p class=\"DocumentTitle\"><br></p></div>\n<h1></h1>\n<div class=\"Contents\">\n<p class=\"First\">The following adverse reactions are discussed in greater detail in another section of the label:</p>\n<dl>\n<dt>•</dt>\n<dd>New Primary Malignancies <span class=\"Italics\">[see Warnings and Precautions (5.1)]</span>\n</dd>\n<dt>•</dt>\n<dd>Hemorrhage <span class=\"Italics\">[see Warnings and Precautions (5.2)]</span>\n</dd>\n<dt>•</dt>\n<dd>Venous Thromboembolism <span class=\"Italics\">[see Warnings and Precautions (5.3)]</span>\n</dd>\n<dt>•</dt>\n<dd>Cardiomyopathy <span class=\"Italics\">[see Warnings and Precautions (5.4)]</span>\n</dd>\n<dt>•</dt>\n<dd>Ocular Toxicities <span class=\"Italics\">[see Warnings and Precautions (5.5)]</span>\n</dd>\n<dt>•</dt>\n<dd>Interstitial Lung Disease <span class=\"Italics\">[see Warnings and Precautions (5.6)]</span>\n</dd>\n<dt>•</dt>\n<dd>Serious Febrile Reactions <span class=\"Italics\">[see Warnings and Precautions (5.7)]</span>\n</dd>\n<dt>•</dt>\n<dd>Serious Skin Toxicity <span class=\"Italics\">[see Warnings and Precautions (5.</span><span class=\"Italics\">8)]</span>\n</dd>\n<dt>•</dt>\n<dd>Hyperglycemia <span class=\"Italics\">[see Warnings and Precautions (5.9)]</span>\n</dd>\n</dl>\n<p class=\"First\"> Because clinical trials are conducted under widely varying conditions, adverse reaction rates observed in the clinical trials of a drug cannot be directly compared to rates in the clinical trials of another drug and may not reflect the rates observed in practice.</p>\n<p>The data described in the Warnings and Precautions section and below reflect exposure to MEKINIST as a single agent and in combination with dabrafenib. </p>\n<p><span class=\"Underline\">MEKINIST Administered as a Single Agent</span></p>\n<p>MEKINIST as a single agent was evaluated in 329 patients including 107 (33%) exposed for greater than or equal to 6 months and 30 (9%) exposed for greater than or equal to one year. MEKINIST as a single agent was studied in open-label, single-arm trials (N = 118) or in an open-label, randomized, active-controlled trial (N = 211). The median age was 54 years, 60% were male, &gt;99% were White, and all patients had metastatic melanoma. All patients received 2 mg once-daily doses of MEKINIST. </p>\n<p>Table 3 presents adverse reactions identified from analyses of Trial 1, a randomized, open-label trial of patients with BRAF V600E or V600K mutation-positive melanoma receiving MEKINIST (N = 211) 2 mg orally once daily or chemotherapy (N = 99) (either dacarbazine 1,000 mg/m<span class=\"Sup\">2</span> every 3 weeks or paclitaxel 175 mg/m<span class=\"Sup\">2</span> every 3 weeks) <span class=\"Italics\">[see Clinical Studies (14.1)]</span>. Patients with abnormal LVEF, history of acute coronary syndrome within 6 months, or current evidence of Class II or greater congestive heart failure (New York Heart Association) were excluded from Trial 1. The median duration of treatment with MEKINIST was 4.3 months. In Trial 1, 9% of patients receiving MEKINIST experienced adverse reactions resulting in permanent discontinuation of trial medication. The most common adverse reactions resulting in permanent discontinuation of MEKINIST were decreased left ventricular ejection fraction (LVEF), pneumonitis, renal failure, diarrhea, and rash. Adverse reactions led to dose reductions in 27% of patients treated with MEKINIST. Rash and decreased LVEF were the most common reasons cited for dose reductions of MEKINIST.</p>\n<table width=\"600pt\">\n<caption><span>Table 3. Selected Adverse Reactions Occurring in ≥10% of Patients Receiving MEKINIST and at a Higher Incidence (≥5%) than in the Chemotherapy Arm or ≥2% (Grades 3 or 4) Adverse Reactions</span></caption>\n<col width=\"41%\">\n<col width=\"15%\">\n<col width=\"15%\">\n<col width=\"15%\">\n<col width=\"15%\">\n<tfoot><tr class=\"First Last\"><td colspan=\"5\">\n<p class=\"First\"><span class=\"Sup\">a</span>National Cancer Institute Common Terminology Criteria for Adverse Events, version 4.0.</p>\n<p><span class=\"Sup\">b</span>Grade 4 adverse reactions limited to rash (n = 1) in trametinib arm and diarrhea (n = 1) in chemotherapy arm.</p>\n<p><span class=\"Sup\">c</span>Includes stomatitis, aphthous stomatitis, mouth ulceration, and mucosal inflammation.</p>\n<p><span class=\"Sup\">d</span>Includes abdominal pain, lower abdominal pain, upper abdominal pain, and abdominal tenderness.</p>\n<p><span class=\"Sup\">e</span>Includes lymphedema, edema, and peripheral edema.</p>\n<p><span class=\"Sup\">f</span>Includes epistaxis, gingival bleeding, hematochezia, rectal hemorrhage, melena, vaginal hemorrhage, hemorrhoidal hemorrhage, hematuria, and conjunctival hemorrhage.</p>\n</td></tr></tfoot>\n<tbody class=\"Headless\">\n<tr class=\"First\">\n<td class=\"Botrule Lrule Rrule Toprule\" align=\"center\" rowspan=\"3\" valign=\"bottom\"><p class=\"First\"><span class=\"Bold\">Adverse Reactions</span></p></td>\n<td class=\"Lrule Rrule Toprule\" align=\"center\" colspan=\"2\" valign=\"middle\"><p class=\"First\"><span class=\"Bold\">MEKINIST</span></p></td>\n<td class=\"Lrule Rrule Toprule\" align=\"center\" colspan=\"2\" valign=\"middle\"><p class=\"First\"><span class=\"Bold\">Chemotherapy</span></p></td>\n</tr>\n<tr>\n<td align=\"center\" colspan=\"2\" valign=\"middle\"><p class=\"First\"><span class=\"Bold\">N = 211</span></p></td>\n<td class=\"Botrule Lrule Rrule\" align=\"center\" colspan=\"2\" valign=\"middle\"><p class=\"First\"><span class=\"Bold\">N = 99</span></p></td>\n</tr>\n<tr>\n<td class=\"Botrule Lrule Rrule Toprule\" align=\"center\" valign=\"top\">\n<p class=\"First\"><span class=\"Bold\">All</span></p>\n<p><span class=\"Bold\">Grades<span class=\"Sup\">a</span></span></p>\n</td>\n<td class=\"Botrule Lrule Rrule Toprule\" align=\"center\" valign=\"top\">\n<p class=\"First\"><span class=\"Bold\">Grades</span></p>\n<p><span class=\"Bold\">3 and 4<span class=\"Sup\">b</span></span></p>\n</td>\n<td class=\"Botrule Lrule Rrule Toprule\" align=\"center\" valign=\"top\">\n<p class=\"First\"><span class=\"Bold\">All</span></p>\n<p><span class=\"Bold\">Grades<span class=\"Sup\">a</span></span></p>\n</td>\n<td class=\"Botrule Lrule Rrule Toprule\" align=\"center\" valign=\"top\">\n<p class=\"First\"><span class=\"Bold\">Grades</span></p>\n<p><span class=\"Bold\">3 and 4<span class=\"Su